import React, { Component } from "react";
import './App.css';
import Navbar from "react-bootstrap/Navbar"
import Button from "react-bootstrap/Button"
import Card from "react-bootstrap/Card"
import Container from "react-bootstrap/Container"

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: [],
      display: "Generate a quote by clicking the button below!"
    };
  }

  render() {
    return (
      <div className="App">
        <Navbar bg="light" expand="lg">
          <Navbar.Brand href="#home">Quote Generator</Navbar.Brand>
        </Navbar>
        <Container>
          <Card bg="warning" text="light" style={{ marginTop: "5vh" }}>
            <Card.Body>
              <Card.Title>Quote</Card.Title>
            </Card.Body>
          </Card>
          <button className="btn btn-lg btn-primary" style={{ marginTop: "10vh" }} onClick={this.handleClick}>
            Generate
        </button>
        </Container>
      </div >
    );
  }
}

export default App;
