import React, { Component } from "react";
import './App.css';
import Navbar from "react-bootstrap/Navbar"
import Button from "react-bootstrap/Button"
import Card from "react-bootstrap/Card"
import Container from "react-bootstrap/Container"

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: [],
      display: "Generate a quote by clicking the button below.",
      randomNumber: 0,
      author: ""
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    fetch("https://type.fit/api/quotes")
      .then((resp) => resp.json())
      .then((data) => this.setState({ quotes: data }));
    console.log(this.state.quotes);
  }

  handleClick() {
    this.setState({
      randomNumber: Math.floor(Math.random() * this.state.quotes.length),
      display: this.state.quotes[this.state.randomNumber].text,
      author: this.state.quotes[this.state.randomNumber].author
    });
  }

  render() {
    return (
      <div className="App">
        <Navbar bg="light" expand="lg">
          <Navbar.Brand href="#home">Quote Generator</Navbar.Brand>
        </Navbar>
        <Container>
          <Card bg="info" text="light" style={{ marginTop: "5vh", height: "500px" }}>
            <Card.Body>
              <Card.Title style={{ fontSize: '40px' }}>Quote</Card.Title>
              <Card bg="light" text="dark" style={{ marginTop: "5vh", height: "40vh", fontSize: "25px" }}>
                <Card.Text>
                  {this.state.display}
                </Card.Text>
                <Card.footer>{this.state.author}</Card.footer>
              </Card>
            </Card.Body>
          </Card>
          <Button className="btn btn-lg btn-secondary" style={{ marginTop: "10vh" }} onClick={this.handleClick}>
            Generate Quote
        </Button>
        </Container>
      </div >
    );
  }
}

export default App;
