import React, { Component } from "react";
import './App.css';
import Navbar from "react-bootstrap/Navbar"
import Button from "react-bootstrap/Button"
import Card from "react-bootstrap/Card"

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: [],
      display: "Generate a quote by clicking the button below!"
    };
  }

  render() {
    return (
      <div className="App">
        <Navbar bg="light" expand="lg">
          <Navbar.Brand href="#home">Quote Generator</Navbar.Brand>
        </Navbar>
        <Card bg="secondary" style={{ width: '80rem', height: '50vh' }}>
          <Card.Body>
            <Card.Title style={{ fontSize: '85px' }}>Intermittent Fasting Timer</Card.Title>
            <p class="text-info" style={{ fontSize: '24px' }}>Pick a fasting time below and watch the clock count down the time until your next meal.</p>
            <Time hours={this.state.hours} value={this.state.value} seconds={this.state.seconds} />
            <button className="btn btn-lg btn-primary" style={{ marginLeft: '25px' }} onClick={this.reset}>Reset</button>
            <button className="btn btn-lg btn-danger" style={{ marginLeft: '75px' }} onClick={this.stop}>Stop</button>
          </Card.Body>
        </Card>
      </div>
    );
  }
}

export default App;
